        axios.get('/api/companies')
        .then((res)=>
        {console.log(res)}
        // setCompanies(res.data)
        )
        .catch((err)=>
        {console.log("error Companies")})
    },[companies])
return (
    <Context.Provider
    value = {{items,companies}}>
        {children}
    </Context.Provider>
    );

};
