import axios from "axios";
import React, { useState } from "react";
import { useContext } from "react";
import { useEffect } from "react";
import { ItemContext } from "../context/Context";
import styled from "styled-components";
import { useHistory } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";

const Checkout = () => {
  const { cart, setCart, Items, error, setError } = useContext(ItemContext);
  const myCart = cart.data;
  console.log(myCart);
  // let quantity;
  let subtotal = 0;
  let total = 0;
  const history = useHistory();

  useEffect(() => {
    axios
      .get("/api/cart")
      .then((res) => {
        setCart(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const deleteItem = (ev) => {
    ev.preventDefault();
    ev.stopPropagation();
    const _id = ev.target.id;
    axios
      .delete(`/api/cart/${_id}`)
      .then((res) => {
        console.log(res.data);
        document.location.reload(true);
      })
      .catch((err) => {
        setError(err);
      });
  };

  //onSubmit, this function first posts everything into orderHistory, and then deletes them
  //all from cart. Then pushes to orderHistory page.
  const handleSubmit = (ev) => {
    ev.preventDefault();
    ev.stopPropagation();
    //posts the order with an id to the orderHistory
    axios({
      method: "POST",
      url: "/api/orderHistory",
      data: {
        data: myCart,
        orderId: uuidv4(),
      },
    });

    //updates items, so that numInStock is reduced by however many of the particular item is in the cart
    myCart.map((item) => {
      const _id = item._id;
      // axios({
      //   method: 'patch',
      //   url: 'get patch endpoint',
      //   data: {
      //     _id: item._id,
      //     name: item.name,
      //     price: item.price,
      //     imageSrc: item.imageSrc,
      //     body_location: item.body_location,
      //     category: item.category,
      //     companyId: item.companyId,
      //     quantity: "figure this out",
      //   }
      // })

      //empties the cart after the order is made
      axios
        .delete(`/api/cart/${_id}`)
        .then((res) => {
          console.log(res.data);
          history.push("/previous-purchases");
        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  if (!cart || !myCart) {
    return <div>Loading</div>;
  } else {
    const priceArray = [];

    return (
      <Wrapper>
        <LeftDiv>
          <HeaderDiv>
            <ShoppingCart>Shopping Cart</ShoppingCart>
            <CartQuantity>{myCart.length} Item(s)</CartQuantity>
          </HeaderDiv>
          <CategoryDiv>
            <Description>Description</Description>
            <Quantity>Quantity</Quantity>
            <Price>Price</Price>
          </CategoryDiv>
          {myCart.map((item) => {
            let quantity = 1;
            let disabled = false;
            let stock = 0;

            Items.map((item2) => {
              if (item2._id === item._id) {
                stock = item2.numInStock;
              }
            });

            const increaseHandler = () => {
              quantity = quantity + 1;
              console.log(quantity);
              if (quantity === stock) {
                disabled = true;
                console.log(disabled);
              }
            };

            //removes $ from each of the prices, turns them into a Number, and pushes to an array.
            const itemPriceNoSymbol = item.price.replace(/\$/g, "");
            const itemPrice = parseInt(itemPriceNoSymbol);
            priceArray.push(itemPrice);
            //checks that we're getting the correct total, then adds all of prices together, creating a subtotal
            if (priceArray.length === myCart.length) {
              const reducer = (accumulator, curr) => accumulator + curr;
              subtotal = priceArray.reduce(reducer);
              //adds tax
              total = (subtotal * 1.15).toFixed(2);
            }

            return (
              <ItemDiv>
                <DescriptionDiv>
                  <Img src={item.imageSrc} alt={item.name} />
                  <SubDescripion>
                    <ItemName>{item.name}</ItemName>
                    <Remove onClick={deleteItem} id={item._id}>
                      Remove
                    </Remove>
                  </SubDescripion>
                </DescriptionDiv>
                <QuantityDiv>
                  <QuantityButton id={item._id}>-</QuantityButton>
                  <ItemQuantity>{quantity}</ItemQuantity>
                  <QuantityButton onClick={increaseHandler} disabled={disabled}>
                    +
                  </QuantityButton>
                </QuantityDiv>
                <PriceDiv>
                  <ItemPrice>{item.price}</ItemPrice>
                </PriceDiv>
              </ItemDiv>
            );
          })}
        </LeftDiv>
        <RightDiv>
          <Form onSubmit={handleSubmit}>
            <Div>Order Summary</Div>
            <Subtotal>
              <Bold>Subtotal</Bold> ${subtotal}
            </Subtotal>
            <Total>
              <Bold>Total</Bold> ${total}
            </Total>
            <Div>Personal Information</Div>
            <TextArea placeholder="Name" required></TextArea>
            <TextArea placeholder="Surname" required></TextArea>
            <TextArea placeholder="Email" required></TextArea>
            <CheckoutButton>Checkout</CheckoutButton>
          </Form>
        </RightDiv>
      </Wrapper>
    );
  }
};
const Bold = styled.span`
  font-weight: bold;
  margin-right: 15px;
`;
const CheckoutButton = styled.button`
  width: 90%;
  border-radius: 15px;
  font-family: var(--secondary-font-family);
  font-size: 25px;
  border: none;
  margin-top: 10px;
  background-color: #ff9966;
  color: white;
  cursor: pointer;
`;
const Subtotal = styled.div`
  font-family: var(--secondary-font-family);
  font-size: 20px;
`;
const Total = styled.div`
  font-family: var(--secondary-font-family);
  font-size: 20px;
  margin-bottom: 15px;
`;
const Div = styled.div`
  font-family: var(--primary-font-family);
  font-size: 30px;
`;
const TextArea = styled.textarea`
  width: 90%;
  resize: none;
  font-family: var(--secondary-font-family);
  font-size: 17px;
`;
const Form = styled.form``;
const SubDescripion = styled.div`
  display: flex;
  flex-direction: column;
`;
const Remove = styled.button`
  background: none;
  border: none;
  color: grey;
  font-family: var(--primary-font-family);
  font-size: 17px;
  width: 30px;
  padding: 0;
  cursor: pointer;
`;
const CategoryDiv = styled.div`
  width: 100%;
  border-bottom: 1px black solid;
  color: grey;
  font-size: 15px;
  display: flex;
`;
const Description = styled.div`
  width: 60%;
  font-family: var(--primary-font-family);
  margin-top: 3px;
  padding-left: 5px;
`;
const DescriptionDiv = styled.div`
  height: 100%;
  width: 60%;
  display: flex;
  padding-right: 10px;
  align-items: center;
`;
const Quantity = styled.div`
  width: 20%;
  font-family: var(--primary-font-family);
  margin-top: 3px;
`;
const CartQuantity = styled.div`
  font-family: var(--primary-font-family);
  font-size: 40px;
`;
const QuantityButton = styled.button`
  background: none;
  border: none;
  font-size: 17px;
  cursor: pointer;
`;
const ItemQuantity = styled.div``;
const QuantityDiv = styled.div`
