import styled from "styled-components"; // styled-components
import { useContext } from "react"; // useContext
import { NavLink } from "react-router-dom"; // for navlinks

//Importing all useContexts
import { ItemContext } from "../context/Context";

//importing icons
import { Icon } from "react-icons-kit";
import { chevronCircleRight } from "react-icons-kit/fa/chevronCircleRight";


// shop page for all items
const ShopPage = () => {
  // getting all items from context
  const { Items } = useContext(ItemContext);

  return (
    <>
      {Items ? (
        <Wrapper>
          <BackgroundImage>
            {/* The grid container houses all the items (GridItem) */}
            <GridContainer>
              {Items.map((item) => {
                return (
                  <>
                  {
                    item.numInStock > 0 ? (
                      <GridItem to={`/shop/items/${item._id}`}>
                      {/* ItemHead is the top half of the item.  */}
                      <ItemHead>
                        <ItemImage src={item.imageSrc} />
                      </ItemHead>
                      {/* ItemBody contains the Items information */}
                      <ItemBody>
                        <ItemDescription>
                          <ItemCaption>Product: </ItemCaption>
                          <ItemName>{item.name}</ItemName>
                        </ItemDescription>
                        <ItemDescription>
                          <ItemCaption>Body Location: </ItemCaption>
                          <ItemLocation>{item.body_location}</ItemLocation>
                        </ItemDescription>
                        <ItemDescription>
                          <ItemCaption>Category: </ItemCaption>
                          <ItemCategory>{item.category}</ItemCategory>
                        </ItemDescription>
                        {/* ButtonSideDiv is where the cart and price are */}
                        <ButtonSideDiv>
                          <ItemPrice>{item.price}</ItemPrice>
                          <AddToCartButton>
                            <Icon
                              size={25}
                              icon={chevronCircleRight}
                              style={{ color: "black" }}
                            />
                          </AddToCartButton>
                        </ButtonSideDiv>
                      </ItemBody>
                    </GridItem>
                    ) : (
                      <></>
                    )
                  }
                  </>
                );
              })}
            </GridContainer>
          </BackgroundImage>
        </Wrapper>
      ) : (
        <AlternateDiv>
        <ImageHere src="https://media.giphy.com/media/JF70qeolvPS0ph52ZY/giphy.gif" />
      </AlternateDiv>
      )}
    </>
  );
};

// styled components
const Wrapper = styled.div`
  position: relative;
  left: 10vw;
  top: 5vh;
  width: 90vw;
  height: 100%;
  display: flex;
  justify-content: space-between;
  background-color: transparent;
`;

const BackgroundImage = styled.div`
  background-image: url("https://i.pinimg.com/736x/82/6a/95/826a95fde43be06c60b5c1f5349587c3.jpg");
  background-repeat: repeat;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
`;

const GridContainer = styled.div`
  width: 90vw;
  display: grid;
  grid-template-columns: auto auto auto;
  padding: 10px;
  background-color: transparent;
  height: 100%;
`;
const GridItem = styled(NavLink)`
  color: black;
  text-decoration: none;
  background-color: white;
  width: 400px;
  height: 550px;
  margin: 15px auto;
  border: 1px solid black;
  border-radius: 25px;
  transition: 0.5s ease-in-out;
  box-shadow: 2px 2px 2px 2px rgba(0, 0, 0, 0.1);
  animation: scaleIn 1s ease-in-out;

  @keyframes scaleIn {
    from {
      transform: translateY(200%);
    }
    to {
      transform: translateY(0%);
    }
  }

  &:hover {
    transform: scale(1.05);
  }
`;
/* comment here */
const ItemHead = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  height: 252px;
  background: #fa782e;
  /* Old browsers */
  background: white;
  /* FF3.6-15 */
  filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#fa782e', endColorstr='#c82930', GradientType=1);
  /* IE6-9 fallback on horizontal gradient */
  border: 4px solid black;
  border-radius: 25px 25px 0 0;
  overflow: hidden;
`;

const ItemImage = styled.img`
  position: relative;
  left: 0;
  margin-top: 5px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ItemBody = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  background-color: black;
  border-radius: 0 0 25px 25px;
  width: 100%;
  height: 55%;
  padding: 15px;
  overflow: hidden;
  color: white;
  margin-bottom: 20px;
`;

const ItemDescription = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-start;
`;
const ItemCaption = styled.div`
  display: inline-block;

  font-size: 1.2rem;
`;

const ItemName = styled.h3`
  display: inline-block;
  margin-left: 50px;
  padding: 10px;
`;
const ItemCategory = styled.h3`
  display: inline-block;
  margin-left: 45px;
  padding: 5px;
`;
const ItemLocation = styled.h3`
  display: inline-block;

  padding: 5px;
`;
const ItemPrice = styled.h4`
  padding: 0 5px;
  display: inline-block;

  width: auto;
  height: 38px;

  background-color: #6ab070;
  -webkit-border-radius: 3px 4px 4px 3px;
  -moz-border-radius: 3px 4px 4px 3px;
  border-radius: 3px 4px 4px 3px;

  border-left: 1px solid #6ab070;

  /* This makes room for the triangle */
  margin-left: 19px;

  position: relative;

  color: white;
  font-weight: 300;
  font-size: 22px;
  line-height: 38px;
  transition: 0.5s ease-in-out;
  padding: 0 10px 0 10px;

  &:before {
    content: "";
    position: absolute;
    display: block;
    left: -19px;
    width: 0;
    height: 0;
    border-top: 19px solid transparent;
    border-bottom: 19px solid transparent;
    border-right: 19px solid #6ab070;
  }

  &:after {
    content: "";
    background-color: white;
    border-radius: 50%;
    width: 4px;
    height: 4px;
    display: block;
    position: absolute;
    left: -9px;
    top: 17px;
  }

  &:hover {
    background-color: white;
    color: black;
    transform: scale(1.2);
    cursor: pointer;
  }
`;

const ButtonSideDiv = styled.div`
  position: relative;
  display: flex;
  justify-content: space-between;
  margin-top: 20px;
  /* align-items: flex-start; */
  width: 100%;
  /* height: 50px; */
  /* padding-left: 50px; */

  align-items: center;
`;

const AddToCartButton = styled.button`
  color: white;
  width: 50px;
  border: none;
  padding: 5px;
  margin-left: 25%;
  border-radius: 5px;
  font-size: 20px;
  font-weight: bold;
  transition: 0.5s ease-in-out;
  &:hover {
    box-shadow: 2px 2px 2px 2px rgba(0, 0, 0, 0.2);
    transform: scale(1.3);
    cursor: pointer;
  }
`;

const AlternateDiv = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 50px;
`;

const ImageHere = styled.img`
  width: 50vw;
  height: auto;
`

export default ShopPage;
